mapper.select:
SELECT
data_date,
ownership_type,
SUM(issued_amount) AS issued_amount,
AVG(avg_interest_rate) AS avg_interest_rate
FROM real_estate_loan_issuance_daily

    region_name = #{globalOverviewRequestQO.regionName}



        AND data_date >= #{globalOverviewRequestQO.startDate}


        AND data_date <= #{globalOverviewRequestQO.endDate}




        AND ownership_type = #{globalOverviewRequestQO.ownershipType}



GROUP BY data_date, ownership_type
ORDER BY data_date ASC

SELECT
